<script>
 
    $(document).ready(function() {

        this.$element     = $("#modal-default");
        this.$content     = this.$element.find('.modal-content');
        var borderWidth   = this.$content.outerHeight() - this.$content.innerHeight();
        var dialogMargin  = $(window).width() > 767 ? 60 : 20;
        var contentHeight = $(window).height() - (dialogMargin + borderWidth);
        var headerHeight  = this.$element.find('.modal-header').outerHeight() || 0;
        var footerHeight  = this.$element.find('.modal-footer').outerHeight() || 0;
        var maxHeight     = contentHeight - (headerHeight + footerHeight);

<?php if ($datagrid->getPrinttableEnabled()) : // printTable?? ?>
        $("#buttonprint").click(function(event) {
            $("#modal-default").addClass('in');
            $("#modal-default").css({'display':'block', 'padding-right':'17px', 'max-height': maxHeight, 'overflow-y': 'auto'});
            $("#modal-dialog").css({'width':'1300px', 'background-color':'#FFF'});
            $.post("index.php?ctl=sessioSetmanalPrint", {<?php echo ($search1 <>'') ? "__s1:".$search1."," : ''; echo ($search2 <>'') ? "__s2:".$search2."," : ''; echo ($search3 <>'') ? "__s3:'".$search3."'," : ''; echo ($search4 <>'') ? "__s4:'".$search4."'," : '';?>}, function(response, status, xhr) {
                if (status == "error") {
                    var msg = "Error! - ";
                    $("#modal-default").html(msg + xhr.status + " " + xhr.statusText);
                } else {
                    $("#modal-dialog").html(response);
                    
                }
            });
        }); 

        $("#button_print").click(function() {
            url = "index.php?ctl=sessioSetmanalPrint<?php echo ($search1 <>'') ? "&__s1=".$search1 : ''; echo ($search2 <>'') ? "&__s2=".$search2 : ''; echo ($search3 <>'') ? "&__s3=".$search3 : ''; echo ($search4 <>'') ? "&__s4=".$search4 : '';?>";
            var NWin = window.open(url, '_blank', 'height=800,width=800');
            if (window.focus) { NWin.focus();}
            return false;
        });
<?php endif ?>

        $("#button_creueta").click(function(event) {
            $("#modal-default").removeClass('in');
            $("#modal-default").css({'display':'none'});
        });
    });



    $("#treetable").treetable({ expandable: true });

    // Highlight selected row
    $("#treetable tbody").on("mousedown", "tr", function() {
        $(".selected").not(this).removeClass("selected");
            $(this).toggleClass("selected");
    });


    $("form#reveal").submit(function() {
        var nodeId = $("#revealNodeId").val()
        try {
            $("#treetable").treetable("reveal", nodeId);
        }
        catch(error) {
            alert(error.message);
        }
        return false;
    });
</script>    
